mapper.select:
SELECT id,title,channel_id,category_id,user_id,created,status,hot,picture,complainCnt From cms_article WHERE deleted = 0
AND status = #{id}
ORDER BY id DESC

SELECT id,title,channel_id,category_id,user_id,created,picture From cms_article WHERE status = 1 AND channel_id = #{channelId}
AND category_id = #{categoryId}
AND deleted = 0 AND hot = 1 ORDER BY id DESC
